mapper.select:
select
id, type, name, path, project_id, directory_id, create_time, modify_time
from dev_management.document


        and id = #{id}


        and type = #{type}


        and name = #{name}


        and path = #{path}


        and project_id = #{projectId}


        and directory_id = #{directoryId}


        and create_time = #{createTime}


        and modify_time = #{modifyTime}
