mapper.select:
select
id, project, from_user, to_user, is_read, title, create_time, modify_time, to_user_id
from dev_management.message


        and id = #{id}


        and project = #{project}


        and from_user = #{fromUser}


        and to_user = #{toUser}


        and is_read = #{isRead}


        and title = #{title}


        and create_time = #{createTime}


        and modify_time = #{modifyTime}


        and to_user_id = #{toUserId}
